import styled from 'styled-components';

export const ExperienceContainer = styled.div`

    .grid-container{
        display: grid;
        grid-template-columns: 1fr 1.2fr 1.5fr;
        grid-template-rows: 1fr 4fr 3fr 2fr;
        grid-template-areas:
            "main    main    header"
            "main    main    candy"
            "mom     yam     candy"
            "footer  footer  candy" ;
        margin:2em;
        height:100vh;
    }
`;
